typedef long double db;
const db eps = 1e-8;
const db pi = acosl(-1.0);

int sgn(db x) {if(fabs(x) < eps) return 0; return x < 0 ? -1 : 1;}

struct Point{
    db x, y;
    Point(){}
    Point(db x, db y) : x(x), y(y){}
    void input(){cin >> x >> y;}
    Point operator + (const Point &b) const{
        return Point(x + b.x, y + b.y);
    }
    Point operator - (const Point &b) const {
        return Point(x - b.x, y - b.y);
    }
    db operator ^ (const Point &b) const{
        return x * b.y - y * b.x;
    }
    db operator * (const Point &b) const{
        return x * b.x + y * b.y;
    }
    Point operator * (const db &k) const{
        return Point(x * k, y * k);
    }
    Point operator / (const db &k) const{
        return Point(x / k, y / k);
    }
    db len(){
        return sqrtl(x * x + y * y);
    }
    db distance(Point p) {
        return (*this-p).len();
    }
};
struct Line{
    Point s, e;
    Line(){}
    Line(Point s, Point e) : s(s), e(e){}
    void input() {s.input(); e.input();}

    int getCrossTypeofSeg(Line v){
        int d1 = sgn((e - s) ^ (v.s - s));  //v.s 在 线段的哪一侧
        int d2 = sgn((e - s) ^ (v.e - s));  //v.e 在 线段的哪一侧
        int d3 = sgn((v.e - v.s) ^ (s - v.s));  
        int d4 = sgn((v.e - v.s) ^ (e - v.s));
        if((d1^d2) == -2 && (d3^d4) == -2) return 2; // 跨立实验满足 一个是-1一个是1

        // 1. v.s在线段上 || v.e 在线段上 || s 在另外一条线段上 || e在另外一条线段上
        return (d1 == 0 &&  sgn((v.s - s) * (v.s - e)) <= 0) ||
            (d2 == 0 && sgn((v.e - s) * (v.e - e)) <= 0) ||
            (d3 == 0 && sgn((s - v.s) * (s - v.e)) <= 0) || 
            (d4 == 0 && sgn((e - v.s) * (e - v.e)) <= 0);
    }

    db getDisPointToLine(Point p) {
        return fabs(((p-s)^(e-s))/(e-s).len());
    }

    db getDisPointToSeg(Point p) {
        if(sgn((e-s) * (p-s)) < 0 || sgn((s - e) * (p - e)) < 0) return min(s.distance(p), e.distance(p));
        return getDisPointToLine(p);
    }
    db getDisSegToSeg(Line v) {
        return min(min(getDisPointToSeg(v.s), getDisPointToSeg(v.e)), min(v.getDisPointToSeg(s), v.getDisPointToSeg(e)));
    }
}l1, l2;

