		if (abs(cross(a, b)) < EPS && dot(a, b) < EPS) return OUT_POLYGON;
		if (a.y > b.y) swap(a, b);
		if (a.y < EPS && EPS < b.y && cross(a, b)) x = !x;
	}
	return ( x ? IN_POLYGON : ON_POLYGON );
}

