double EPS = 1e-10;

double add(double a, double b) {
	if (abs(a + b) < EPS * (abs(a) + abs(b))) return 0;
	return a + b;
}

struct P {
	double x, y;
	P() {}
	P(double x, double y) : x(x), y(y) {
	}
	P operator +(P p) {
		return P(add(x, p.x), add(y, p.y));
	}
	P operator -(P p) {
		return P(add(x, -p.x), add(y, -p.y));
	}
	P operator *(double d) {
		return P(d*x, d*y);
	}
	double dot(P p) {
		return add(x*p.x, y*p.y);
	}
	double det(P p) {
		return add(x*p.y, -y*p.x);
	}
	void print() {
		printf("%.10lf %.10lf \n", x, y);
		return;
	}
};

double dist(P &p, P &q, P &r){
	if((q-p).dot(r-p)*(p-q).dot(r-q) >= 0){
		double a = q.y-p.y;
		double b = q.x-p.x;
		double c = -p.x*q.y+p.y*q.x;
		return fabs(a*r.x+b*r.y+c)/sqrt(a*a+b*b);
	}else{
		return min (sqrt((r.x-p.x)*(r.x-p.x)+(r.y-p.y)*(r.y-p.y)), sqrt((r.x-q.x)*(r.x-q.x)+(r.y-q.y)*(r.y-q.y)));
	}

}

bool isCross(P &p0,P &p1,P &p2,P &p3){
	bool a,b,c,d;
	a = ( (p1-p0).dot(p2-p0)*(p0-p1).dot(p2-p1) >= 0 );
	b = ( (p1-p0).dot(p3-p0)*(p0-p1).dot(p3-p1) >= 0 );
	c = ( (p1-p0).det(p2-p0)*(p1-p0).det(p3-p0) <=0 );
	return a && b && c ;
}

double Distance(P &p0,P &p1,P &p2,P &p3){
	if(isCross(p0, p1, p2, p3)){
		return 0;
	}
	double d;
	d = min(dist(p0, p1, p2), dist(p0, p1, p3));
	d = min(d, dist(p2, p3, p0));
	d = min(d, dist(p2, p3, p1));
	return d;
}

