static const int IN_POLYGON = 2;//????§???¢????????¨
static const int OUT_POLYGON = 1;//????§???¢????????¨
static const int ON_POLYGON = 0;//????§???¢????????????
int contains(Polygon g, Point p) {
	int n = g.size();
	bool x = false;
	rep(i, n) {
		Point a = g[i] - p, b = g[( i + 1 ) % n] - p;
		if (abs(cross(a, b)) < EPS && dot(a, b) < EPS) return OUT_POLYGON;
		if (a.y > b.y) swap(a, b);
		if (a.y < EPS && EPS < b.y && cross(a, b)) x = !x;
	}
	return ( x ? IN_POLYGON : ON_POLYGON );
}

