    double x1, x2, x3, x4, y1, y2, y3, y4;
    Segment s1, s2;
    cin >> q;
    for (int i = 0; i != q; ++i)
    {
        cin >> x1 >> y1 >> x2 >> y2 >> x3 >> y3 >> x4 >> y4;
        s1.p1 = Point(x1, y1);
        s1.p2 = Point(x2, y2);
        s2.p1 = Point(x3, y3);
        s2.p2 = Point(x4, y4);
        cout << fixed << setprecision(10) << getDistance(s1, s2) << endl;
    }
    return 0;
}

