Polygon ConvexHull(Polygon s, bool containsPointOnline = false) {
	Polygon u, l;
	if (s.size() < 3) return s;
	sort(s.begin(), s.end());
	u.push_back(s[0]); 
	u.push_back(s[1]);
	l.push_back(s[s.size() - 1]);
	l.push_back(s[s.size() - 2]);
	auto conditionContaining = [](int ccw, bool containsPointOnline)->bool{
		if (containsPointOnline) return ccw == COUNTER_CLOCKWISE;
		else return ccw != CLOCKWISE;
	};
	for (int i = 2; i < s.size(); i++) {//?????´?????°???
		for (int n = u.size(); ( n >= 2 ) && conditionContaining( ccw(u[n - 2], u[n - 1], s[i]),containsPointOnline); n--) {
			u.pop_back();
		}
		u.push_back(s[i]);
	}
	for (int i = s.size() - 3; i >= 0; i--) {//?????´?????°???
		for (int n = l.size(); n >= 2 && conditionContaining( ccw(l[n - 2], l[n - 1], s[i]),containsPointOnline); n--)l.pop_back();
		l.push_back(s[i]);
	}
	reverse(l.begin(), l.end());
	for (int i = u.size() - 2; i >= 1; i--) l.push_back(u[i]);
	double tmpMax = l[0].y;
	int rotIndex = 0;
	REP(i,1,l.size()) {//?????¶??????????????????????????????????????´??????
		if (l[i].y < tmpMax) {
			rotIndex = i;
			tmpMax = l[i].y;
		}
	}
	rotate(l.begin(), l.begin() + rotIndex, l.end());
	return l;
}
