//点类 
struct P
{
	db x,y;
	P(){}
	P(db _x,db _y):x(_x),y(_y){}
	P operator + (const P& a) const {return (P){x+a.x,y+a.y};}
	P operator - (const P& a) const {return (P){x-a.x,y-a.y};}
	P operator * (db a) const {return (P){x*a,y*a};}
	P operator / (db a) const {return (P){x/a,y/a};}
	bool operator < (const P& a)const
	{return cmp(x,a.x)?cmp(x,a.x)==-1:cmp(y,a.y)==-1;}
	bool operator == (const P& a)const
	{return cmp(x,a.x)==0&&cmp(y,a.y)==0;}
	db alpha(){return atan2(y,x);}
	db abs2(){return x*x+y*y;}
	db abs(){return sqrt(abs2());}
	db disTo(P a){return (*this-a).abs();}
	db dot(P a){return x*a.x+y*a.y;}
	db det(P a){return x*a.y-y*a.x;}
	P unit(){return *this/abs();}
	P rot(db an){return (P){x*cos(an)-y*sin(an),x*sin(an)+y*cos(an)};}
	P rot90(){return (P){-y,x};}
	int quad() const {return sign(y)==1||(sign(y)==0&&sign(x)>=0);}
	void read(){db _x,_y;scanf("%lf%lf",&_x,&_y);x=_x;y=_y;}
	void write(){printf("%.10f %.10f\n",x,y);}
};
bool angcmp(P a,P b){return a.quad()!=b.quad()?a.quad()<b.quad():sign(a.det(b))==-1;}
bool inmid(P a,P m,P b){return inmid(a.x,m.x,b.x)&&inmid(a.y,m.y,b.y);}
db rad(P a,P b){return atan2l(a.det(b),a.dot(b));}
db cro(P q,P p1,P p2){return (p1.x-q.x)*(p2.y-q.y)-(p1.y-q.y)*(p2.x-q.x);}
int croOp(P q,P p1,P p2){return sign(cro(q,p1,p2));}
//线段相交
bool intersect(db l1,db r1,db l2,db r2)
{
	if(l1>r1)swap(l1,r1);if(l2>r2)swap(l2,r2);
	return !(cmp(r1,l2)==-1||cmp(r2,l1)==-1);
}
bool isSS(P p1,P p2,P q1,P q2)
{
	return intersect(p1.x,p2.x,q1.x,q2.x)&&intersect(p1.y,p2.y,q1.y,q2.y)
	&&croOp(p1,p2,q1)*croOp(p1,p2,q2)<=0
	&&croOp(q1,q2,p1)*croOp(q1,q2,p2)<=0;
}
bool isSS_strict(P p1,P p2,P q1,P q2)
{
	return croOp(p1,p2,q1)*croOp(p1,p2,q2)<0
	&&croOp(q1,q2,p1)*croOp(q1,q2,p2)<0;
}
//投影 
P proj(P p1,P p2,P q)
{
	P dir=p2-p1;
	return p1+dir*(dir.dot(q-p1)/dir.abs2());
}
//最近点 
db nearest(P p1,P p2,P q)
{
	P h=proj(p1,p2,q);
	return inmid(p1,h,p2)?q.disTo(h):min(p1.disTo(q),p2.disTo(q));
}
//线段距离 
db disSS(P p1,P p2,P q1,P q2)
{
	if(isSS(p1,p2,q1,q2)) return 0;
	return min(min(nearest(p1,p2,q1),nearest(p1,p2,q2)),min(nearest(q1,q2,p1),nearest(q1,q2,p2)));
}
