        return Point(x - p.x, y - p.y);
    };

    Point operator* (double k){
        return Point(x * k, y * k);
    }

    Point operator/ (double k){
        return Point(x / k, y / k);
    }

    double norm(){
        return x * x + y * y;
    }

    double abs(){
        return sqrt(norm());
    }

    bool operator == (const Point &p) const{
        return fabs(x - p.x) < EPS && fabs(y - p.y) < EPS;
    }
};

typedef Point Vector;

double dot(Vector a, Vector b){
    return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b){
    return a.x * b.y - a.y * b.x;
}

bool isParallel(Vector a, Vector b){
    return equals(cross(a, b), 0.0);
}

bool isOrthogonal(Vector a, Vector b){
    return equals(dot(a, b), 0.0);
}
;
double getCos(Vector a, Vector b){
    return dot(a, b) / (a.abs() * b.abs());
}

double dist(Point p1, Point p2){
    return sqrt(pow((p1.x - p2.x), 2) + pow((p1.y - p2.y), 2));
}

Point projection(Point p1, Point p2, Point p0){
    if(p1 == p0){
        return p0;
    }
    if(p2 == p0){
        return p0;
    }
    return p1 + (p2 - p1) / (p2 - p1).abs() * (p0 - p1).abs() * getCos(p2 - p1, p0 - p1);
}

Point reflection(Point p1, Point p2, Point p0){
    Point p = projection(p1, p2, p0);
    return p0 + (p - p0) * 2;
}

double getDistanceSP(Point p1, Point p2, Point p0){
    if(dot(p2 - p1, p0 - p1) < 0.0) return (p0 - p1).abs();
    if(dot(p1 - p2, p0 - p2) < 0.0) return (p0 - p2).abs();
    return (projection(p1, p2, p0) -  p0).abs();
}

double getDistance(Point p0, Point p1, Point p2, Point p3){
    return min(min(getDistanceSP(p0, p1, p2),
                getDistanceSP(p0, p1, p3)),
                min(getDistanceSP(p2, p3, p0),
                getDistanceSP(p2, p3, p1)));
}


