double DistancePS(Point p, Point a, Point b) {
    if (a == b) return Length(p - a);
    Vector v1 = b - a, v2 = p - a, v3 = p - b;
    if (Dot(v1, v2) < 0) return Length(v2);
    if (Dot(v1, v3) > 0) return Length(v3);
    return fabs(Cross(v1, v2)) / Length(v1);
}
bool IntersectSS(Point a1, Point a2, Point b1, Point b2) {
    double c1 = Cross(a2 - a1, b1 - a1), c2 = Cross(a2 - a1, b2 - a1);
    double c3 = Cross(b2 - b1, a1 - b1), c4 = Cross(b2 - b1, a2 - b1);
    return dcmp(c1) * dcmp(c2) < 0 && dcmp(c3) * dcmp(c4) < 0;
}
 double DistanceSS(Point a1, Point a2, Point b1, Point b2) {
    if (IntersectSS(a1, a2, b1, b2)) return 0.0;
    return min(min(DistancePS(a1, b1, b2), DistancePS(a2, b1, b2)),
               min(DistancePS(b1, a1, a2), DistancePS(b2, a1, a2)));
}

