int intersectCL(Circle c,Line l,Point *p,Point *q){
    if(norm(projection(l,c.p) - c.p) - c.r*c.r > EPS)return 0;
    double d1 = pabs(c.p-l[0]);
    double d2 = pabs(c.p-l[1]);
    if(d1 < c.r + EPS && d2 < c.r + EPS)return 0;
    if(d1 < c.r - EPS && d2 > c.r+EPS
        || d1 > c.r + EPS && d2 < c.r -EPS)return 1;
    Point h = projection(l,c.p);
    if(dot(l[0] - h,l[1] - h) < 0)return 2;
    return 0;
}

int intersectCC(Circle a,Circle b,Point *p,Point *q){
    double dist = pabs(a.p-b.p);
    if(dist < fabs(a.r-b.r) || dist > a.r+b.r){
        //????????????????????´??????????????¨???
        //????????????????????¨???????????¨???
        *p = Point(1e15,1e15);
        *q = Point(1e15,1e15);
        return 0;
    }
    else if(fabs(dist-a.r-b.r) < EPS){
        //??????1?????§????????£???????????¨???
        Point way;
        way = b.p-a.p;
        *p = a.p+UnitVector(way)*a.r;
        *q = *p;
        return 1;
    }
    else{
        //??????2?????§????????£???????????¨???
        if(a.r < b.r){
            swap(a.r,b.r);
            swap(a.p,b.p);
        }
        double s = (a.r + b.r + dist) / 2.0 ;
        double area = sqrt(s * (s - a.r) * (s - b.r) * (s - dist));
        double h = 2.0 * area / dist;

        Point  v = b.p-a.p;
        v = UnitVector(v);
        Point  m = a.p + (v*sqrt(a.r*a.r-h*h));
        Point  n = v * Point(0.0,1.0);

        *p = m + (n * h);
        *q = m - (n * h);
        return 2;
    }
}

