typedef double ll;

struct Point
{
    ll x,y;
    Point(){ x = y = 0.0; }
    Point(ll _x, ll _y) : x(_x), y(_y){}

    Point operator+ (Point p)
    {
        return Point(p.x + x, p.y + y);
    }

    Point operator- (Point p)
    {
        return Point(x - p.x, y - p.y);
    }

    ll operator* (Point p)
    {
        return p.x * x + p.y * y;
    }

    Point operator* (ll t)
    {
        return Point(x*t, y*t);
    }

    ll operator~ ()
    {
        return sqrt(x * x + y * y);
    }

    ll operator% (Point p)
    {
        return x * p.y - y * p.x;
    }

};

bool areIntersectSegment(Point a, Point b, Point p, Point q)
{
    Point v1=(b-a);
    Point v2=(q-p);
    double t,t1;
    if( v1 % v2 != 0 )
    {
        t = (double)(( p - a ) % v2) / (double)(v1 % v2);
        t1 = (double)(( a - p ) % v1) / (double)(v2 % v1);
        if( t >= 0.0 && t <= 1.0 && t1 >= 0.0 && t1 <= 1.0) return true;
        else return false;
    }
    else
    {
        // Segment collinear
        if( v1 % v2 == 0 && (p - a) % v1 == 0 )
        {
            t = (double)((p - a)*v1) /  (double)(v1*v1);
            t1 = (double)((p+v2-a)*v1) / (double)(v1*v1);
            //If are in opposite direction
            if( v1 * v2 < 0 )
            {
                if( t1 <= 1.0 && 0.0 <= t ) return true;
            }
            else
            {
                if( t <= 1.0 && 0.0 <= t1 ) return true;
            }
        }
    }
    return false;
}

Point projection(Point p1, Point p2, Point o)
{
    Point a = (p2 - p1);
    Point b = (o - p1);
    double t =( (a * b) / (a * a) );
    if( t < 0.0 ) return p1;
    if( t > 1.0 ) return p2;
    return p1 + a * t;
}

double distanceSegment(Point a, Point b, Point p, Point q)
{
    double minimo = numeric_limits<double>::max();
    Point aux;
    if( !areIntersectSegment(a,b,p,q) )
    {
        minimo = min( minimo, ~(projection(a,b,p)-p) );
        minimo = min( minimo, ~(projection(a,b,q)-q) );
        minimo = min( minimo, ~(projection(p,q,a)-a) );
        minimo = min( minimo, ~(projection(p,q,b)-b) );
        return  minimo;
    }
    return 0.0;
}

