using namespace std;

const double eps = 1e-9;

bool same(double a, double b) { return a == b; }

struct P {
    double x, y;
    P() : x(0), y(0) {}
    P(double x, double y) : x(x), y(y) {}
    P operator + (P b) { return P(x + b.x, y + b.y); }
    P operator - (P b) { return P(x - b.x, y - b.y); }
    P operator * (double b) { return P(x * b, y * b); }
    P operator / (double b) { return P(x / b, y / b); }
    double operator * (P b) { return x * b.x + y * b.y; }
    double operator ^ (P b) { return x * b.y - y * b.x; }
    double abs() { return hypot(x, y); }
    P unit() { return *this / abs(); }
};

struct L{
    // ax + by + c = 0
    double a, b ,c, o;
    P pa, pb;
    L() : a(0), b(0), c(0), o(0), pa(), pb() {}
    L(P pa, P pb) : a(pa.y - pb.y), b(pb.x - pa.x), c(pa ^ pb), o(atan2(-a, b)), pa(pa), pb(pb) {}
    P project(P p) { return pa + (pb - pa).unit() * ((pb - pa) * (p - pa) / (pb - pa).abs()); }
    P reflect(P p) { return p + (project(p) - p) * 2; }
    double get_ratio(P p) { return (p - pa) * (pb - pa) / ((pb - pa).abs() * (pb - pa).abs()); }
    bool inside(P p) {
        double z = get_ratio(p);
        return z >= 0 && z <= 1;
    }
};

bool parallel(L x, L y) { return same(x.a * y.b, x.b * y.a); }

P intersect(L x, L y) { return P(-x.b * y.c + x.c * y.b, x.a * y.c - x.c * y.a) / (-x.a * y.b + x.b * y.a); }

int main() {
    int q; cin >> q; while (q--) {
        P p[4]; 
        for (int i = 0; i < 4; ++i) cin >> p[i].x >> p[i].y;
        L l1(p[0], p[1]), l2(p[2], p[3]);
        double ans = 1e12;
        auto update = [&] (P &p, L &l) {
            P z = l.project(p);
            if (l.inside(z)) ans = min(ans, (p - z).abs());
            ans = min({ans, (p - l.pa).abs(), (p - l.pb).abs()});
        };
        if (parallel(l1, l2)) {
            if ((p[2] - l1.project(p[2])).abs() < eps) {
                if (l1.inside(p[2]) || l1.inside(p[3]) || l2.inside(p[0]) || l2.inside(p[1])) ans = 0;
                else ans = min({ans, (p[0] - p[2]).abs(), (p[0] - p[3]).abs(), (p[1] - p[2]).abs(), (p[1] - p[3]).abs()});
            } else {
                update(p[2], l1);
                update(p[3], l1);
                update(p[0], l2);
                update(p[1], l2);
            }
        } else {
            P z = intersect(l1, l2);
            if (l1.inside(z) && l2.inside(z)) ans = 0;
            else {
                update(p[2], l1);
                update(p[3], l1);
                update(p[0], l2);
                update(p[1], l2);
            }
        }
        cout << fixed << setprecision(12) << ans << endl;
    }
}

#ifndef ___GEOMETRY_LIBRARY
