}
int ccw( Point p1, Point p2,  Point p3){			//判断三点的相对位置 
	Vector v1=p2-p1,v2=p3-p1;
	if(cross(v1,v2)<-EPS) return CLOCKWISE;
	if(cross(v1,v2)>EPS) return COUNTER_CLOCKWISE;
	if(dot(v1,v2)<-EPS) return ONLINE_BACK;
	if(norm(v1)<norm(v2)) return ONLINE_FRONT;
	return ON_SEGMENT;
}
bool intersect(Point p1,Point p2,Point p3,Point p4){//线段相交 
	return (ccw(p1,p2,p3)*ccw(p1,p2,p4)<=0&&
				ccw(p3,p4,p1)*ccw(p3,p4,p2)<=0);
}
bool intersect(Segment s1,Segment s2){
	return intersect(s1.p1,s1.p2,s2.p1,s2.p2);
}
double distanceLP(Line l,Point p){					//点到直线的距离 
	Vector v1=l.p2-l.p1,v2=p-l.p1;
	return fabs(cross(v1,v2))/abs(v1);
}
double distanceSP(Segment s,Point p){				//点到线段的距离
	if(dot(p-s.p2,s.p1-s.p2)>EPS&& dot(p-s.p1,s.p2-s.p1)>EPS) 
		return distanceLP(s,p);	
	double len1=abs(s.p1-p),len2=abs(s.p2-p);
	return min(len1,len2);
}
double distanceSS(Segment s1,Segment s2){			//两线段间距离
	if(intersect(s1,s2)) return 0.0;
	double len[]={distanceSP(s1,s2.p1),distanceSP(s1,s2.p2),
					distanceSP(s2,s1.p1),distanceSP(s2,s1.p2)};
	double minlen=len[0];
	for(int i=1;i<4;i++)
		if(minlen>len[i]) minlen=len[i];
	return minlen;
}
