// minus: left, plus: right, 0: colinear
int Direction(const Point & p0, const Point & p1, const Point & p2) {
  Vector v1(p1.x - p0.x, p1.y - p0.y);
  Vector v2(p2.x - p0.x, p2.y - p0.y);
  return dcmp(v2.Cross(v1));
}

// minus: back, plus: front, 0: on
int SegmentDirection(const Point & p0, const Point & p1, const Point & p2) {
  if (p2.x >= min(p0.x, p1.x) && p2.x <= max(p0.x, p1.x)
      && p2.y >= min(p0.y, p1.y) && p2.y <= max(p0.y, p1.y)) { return 0; }
  if ((p2.x - p0.x) * (p1.x - p0.x) > 0
      || (p2.y - p0.y) * (p1.y - p0.y) > 0) { return 1; }
  return -1;
}

bool IsIntersecting(const Line & l1, const Line & l2) {
  auto d1 = Direction(l2.p1, l2.p2, l1.p1);
  auto d2 = Direction(l2.p1, l2.p2, l1.p2);
  auto d3 = Direction(l1.p1, l1.p2, l2.p1);
  auto d4 = Direction(l1.p1, l1.p2, l2.p2);

  if (d1 * d2 < 0 && d3 * d4 < 0) { return true; }
  if (d1 == 0 && SegmentDirection(l2.p1, l2.p2, l1.p1) == 0) { return true; }
  if (d2 == 0 && SegmentDirection(l2.p1, l2.p2, l1.p2) == 0) { return true; }
  if (d3 == 0 && SegmentDirection(l1.p1, l1.p2, l2.p1) == 0) { return true; }
  if (d4 == 0 && SegmentDirection(l1.p1, l1.p2, l2.p2) == 0) { return true; }
  return false;
}

double Distance(const Point & p1, const Point & p2) {
  return hypot(p2.x - p1.x, p2.y - p1.y);
}

