struct Line{
    Point begin,end;

    Line(){
        begin = Point(-1,-1),end = Point(1,1);
    }
    Line(const Point& b,const Point&e){
        begin = b,end = e;
    }
    //ax+by+c = 0
    Line(const double a,const double b,const double c){
        if (sgn(a) == 0 && sgn(b) == 0){
            assert(-1);
        }
        if (sgn(b) == 0){
            begin = Point(-c/a,0.0);
            end = Point(-c/a,1.0);
        }
        else{
            begin = Point(0.0,-c/b);
            end = Point(1.0,-(a+c)/b);
        }
    }
    Point vec() const{
        return end - begin;
    }
    Point cvec() const{
        return begin - end;
    }

};

typedef Line Ray;
typedef Line Segment;

int parallel(const Line& l1,const Line& l2){
    if ((sgn(abs(l1.begin-l2.begin)) == 0 && sgn(abs(l1.end-l2.end)) == 0) || (sgn(abs(l1.begin-l2.end)) == 0 && sgn(abs(l1.end-l2.begin)) == 0)){
        return -1;
    }
    else if (sgn(cross(l1.vec(),l2.vec())) == 0){
        return 1;
    }
    else{
        return 0;
    }
}

Point lineIntersection(const Line& l1,const Line& l2){
    return l1.begin+l1.vec()*abs(cross(l2.end-l1.begin,l2.vec())/cross(l1.vec(),l2.vec()));
}

pair<int,Point> segmentIntersection(const Segment& s1,const Segment& s2){
    if (pplace(s1.begin,s1.end,s2.begin)*pplace(s1.begin,s1.end,s2.end) <= 0 &&
    pplace(s2.begin,s2.end,s1.begin)*pplace(s2.begin,s2.end,s1.end) <= 0){
        if (sgn(cross(s1.vec(),s2.vec())) == 0){
            return make_pair(-1,Point(0,0)); //parallel
        }
        else{
            return make_pair(1,lineIntersection(s1,s2));
        }
    }
    return make_pair(0,Point(0,0));
}

double distPointLine(const Point& p,const Line& l){
    return abs(cross(l.vec(),p-l.begin)/abs(l.vec()));
}

double distPointSegment(const Point& p,const Segment& s){
    if (sgn(dot(s.vec(),p-s.begin)) < 0 || sgn(dot(s.cvec(),p-s.end)) < 0){
        return min(abs(p-s.begin),abs(p-s.end));
    }
    return distPointLine(p,s);
}

double distSegmentSegment(const Segment& s1,const Segment& s2){
    if (segmentIntersection(s1,s2).first){
        return 0.0;
    }
    return min({distPointSegment(s1.begin,s2)
            ,distPointSegment(s1.end,s2)
            ,distPointSegment(s2.begin,s1)
            ,distPointSegment(s2.end,s1)});
}

