      cin >> x0 >> y0 >> x1 >> y1 >> x2 >> y2 >> x3 >> y3;

      Distance d( Vector(x0, y0), Vector(x1, y1), Vector(x2, y2), Vector(x3, y3));

      d.solve();
    }      

  return 0;
}

