double distance_point_to_point(const Point & p1, const Point & p2)
{
    return norm(p1-p2);
}

double distance_PL(const Point & p, const Line & l)
{
    return fabs((p-l.point_online)*l.get_normal())/norm(l.get_normal());
}

double distance_point_to_segment(const Point & p, const Segment & s)
{
    if (check_intersection(s, Line(p, p+s.get_normal())))
        return distance_PL(p, Line(s.first_end, s.second_end));
    return min(distance_point_to_point(p, s.first_end), distance_point_to_point(p, s.second_end));
}

double distance_PS(const Point & p, const Segment & s)
{
    // if (check_intersection(s, Line(p, p+s.get_normal())))
    //     return fabs((p-s.first_end)*s.get_normal())/norm(s.get_normal());
    // return min(distance_point_to_point(p, s.first_end), distance_point_to_point(p, s.second_end));

    if ((s.first_end-s.second_end)*(p-s.second_end) < 0)
        return distance_point_to_point(p, s.second_end);
    if ((s.second_end-s.first_end)*(p-s.first_end) < 0)
        return distance_point_to_point(p, s.first_end);
    return distance_PL(p, Line(s.first_end, s.second_end));
}


double distance_segment_to_segment(const Segment & s1, const Segment & s2)
{
    if (check_intersection(s1, s2))
        return 0;
    double d1 = min(distance_point_to_segment(s1.first_end, s2), distance_point_to_segment(s1.second_end, s2));
    double d2 = min(distance_point_to_segment(s2.first_end, s1), distance_point_to_segment(s2.second_end, s1));
    return min(d1, d2);
}

