                &x1, &y1, &x2, &y2, &x3, &y3, &x4, &y4);
        Segment s1, s2;
        s1.p1 = Point(x1, y1);
        s1.p2 = Point(x2, y2);
        s2.p1 = Point(x3, y3);
        s2.p2 = Point(x4, y4);
        double ret = getDistance(s1, s2);
        printf("%.10f\n", ret);
    }

    return 0;
}

