	}
	else if (innerc_b < zero) {
		return distance(p, s.p2);
	}
	else {
		return fabs(outerProduct(a, b) / absolute(b) );
	}
}

template<typename T>
bool intersects(const Segment<T>& s1, const Segment<T>& s2) {
	function<bool(Segment<T>, Segment<T>)> intersectsFromOneSegment//one segment : s1
		= [&](const Segment<T>& s1, const Segment<T>& s2){
			const Point<T> a = s2.p1 - s1.p1;
			const Point<T> c = s2.p2 - s1.p1;
			const Point<T> b = s1.p2 - s1.p1;

			return outerProduct(b, a) > 0 != outerProduct(b, c) > 0;
	};
	return intersectsFromOneSegment(s1, s2) && intersectsFromOneSegment(s2, s1);
}

template<typename T>
T getDistanceFromSegmentToSegment(const Segment<T>& s1, const Segment<T>& s2) {
	if (intersects(s1, s2)) {
		return static_cast<T>(0.0);
	}else{
		return min(
			min(getDistanceFromPointToSegment(s1.p1, s2), getDistanceFromPointToSegment(s1.p2, s2))
			, min(getDistanceFromPointToSegment(s2.p1, s1), getDistanceFromPointToSegment(s2.p2, s1))
		);
	}
}

