  bool parallel(Vector2 &other) {
    return fequals(fabs(dot(other)), length() * other.length());
  }

  bool orthogonal(Vector2 &other) {
    return fequals(0, dot(other));
  }

  bool operator < (const Vector2 &v) {
    return x != v.x ? x < v.x : y < v.y;
  }

