res.y=-a.y;
return res;
}


int ccw(point p0,point p1,point p2){
point a=vec(p1,p0);
point b=vec(p2,p0);
	if(cross(a,b)>eps){
	return 1;//counter_clockwise
	}else if(cross(a,b)<eps){
	return -1;//clockwise
	}else if(dot(a,b)<-eps){
	return 2;//online_back
	}else if(absolute(a)<absolute(b)){
	return -2;//online_front
	}else{
	return 0;//on_segment
	}

}
//交差判定　s1-s2 s3-s4
bool intersect(point s1,point s2,point s3,point s4){
 if(ccw(s1,s2,s3)*ccw(s1,s2,s4)<=0 && ccw(s3,s4,s1)*ccw(s3,s4,s2)<=0)
        return true;
    return false;
}
bool intersect(line l1,line l2){
	return intersect(l1.a,l1.b,l2.a,l2.b);
}
//交点
point crosspoint(line l1,line l2){
	point veca=vec(l1.b,l1.a);
	point vecb=vec(l2.b,l2.a);
	double d= cross(veca,vecb);
	double e= cross(vecb,vec(l2.b,l1.a));
	return vec(l1.a,product(veca,e/d));

}

//距離　point-point 
double distance (point a,point b){
return norm(vec(a,b));
}
//距離 point-line
double distance(line l,point p){
return abs(cross(vec(p,l.a),vec(l.b,l.a))/norm(vec(l.b,l.a)));
}
//距離　line-line
double distance(line a,line b){
	if(intersect(a,b))return 0;
	double m= distance(a.a,b.a);
	m=min(m,distance(a.a,b.b));
	m=min(m,distance(a.b,b.a));
	m=min(m,distance(a.b,b.b));
	return m;
}

