/*角abcの角度　a(xa,ya), b(xb,yb), c(xc,yc),*/
/*2辺の角度*/
/*aとb　またはbとcが重なっていればPIを返す*/
double angle_of_the_two_edges(double ax, double ay, double bx, double by, double cx, double cy){
    if(ax==bx&&ay==by)return M_PI;
    if(cx==bx&&cy==by)return M_PI;

    double angle1 = angle_of_the_line(ax, ay, bx, by);
    double angle2 = angle_of_the_line(bx, by, cx, cy);

    double ret = angle1+(M_PI-angle2);
    if(ret>M_PI * 2)ret -= M_PI * 2;
    if(ret<0)ret += M_PI * 2;
    return /*2*M_PI- */ret;

}

//-------------------------------------------------------------------------------------
/*多角形に対する点の内外判定*/
/* n頂点の多角形p_xy[n]に対して点(x,y)の内外判定を行う*/
/* 内なら2 線上なら1 外なら0*/
int inside_and_outside_the_determination_of_the_polygon_and_point(int n, double p_xy[10000][2], double x, double y){
    //点が線分上にあるか
    for(int i=0; i<n; i++){
        double l = distance_between_the_line_segment_and_point(x, y, p_xy[i][0], p_xy[i][1], p_xy[(i+1)%n][0], p_xy[(i+1)%n][1]);
        if(l==0.0)return 1;
    }

    //点が中にあるか外にあるか
    int ans = 0;
    for(int i=0; i<n; i++){
        if(p_xy[i][0]!=p_xy[(i+1)%n][0]){
            ans += line_segment_cross_decision(p_xy[i][0], p_xy[i][1], p_xy[(i+1)%n][0], p_xy[(i+1)%n][1], x, y, x, y+1000000.0);

            if(distance_between_the_line_segment_and_point(p_xy[i][0], p_xy[i][1],x, y, x, y+1000000.0)==0.0){
                if(p_xy[i][0]>p_xy[(i+1)%n][0])ans--;
            }
            if(distance_between_the_line_segment_and_point(p_xy[(i+1)%n][0], p_xy[(i+1)%n][1],x, y, x, y+1000000.0)==0.0){
                if(p_xy[i][0]<p_xy[(i+1)%n][0])ans--;
            }
        }

    }
    //cout<<" "<<ans<<" ";
    if(ans%2==0)return 0;
    else return 2;
}
