// 2直線の交点 (crosspoint) *******************
Point crosspointLL(Point a1, Point a2, Point b1, Point b2){
    LD d1 = cross(b2-b1, b1-a1);
    LD d2 = cross(b2-b1, a2-a1);
    if (EQ(d1, 0) && EQ(d2, 0)) return a1;
    if (EQ(d2, 0)) throw "not exist crosspoint";
    return a1 + d1/d2 * (a2-a1);
}
