}
bool vec2d::operator==(const vec2d&v)const {
	return x == v.x && y == v.y;
}
bool vec2d::operator!=(const vec2d&v)const {
	return !operator== (v);
}
double vec2d::norm() const {
	return sqrt(x * x + y * y);
}
double vec2d::inner_product(const vec2d&v0, const vec2d&v1) {
	return v0.x * v1.x + v0.y * v1.y;
}
double vec2d::cros_product(const vec2d&v0, const vec2d&v1) {
	return v0.x * v1.y - v0.y * v1.x;
}
double vec2d::argument(const vec2d&v0, const vec2d&v1, bool is_signed) {
	double ret = atan2(cros_product(v0, v1), inner_product(v0, v1));
	return is_signed ? ret : abs(ret);
}
double vec2d::distance(const vec2d&v0, const vec2d&v1) {
	return hypot(v1.x - v0.x, v1.y - v0.y);
}
vec2d vec2d::projection(const vec2d&scr, const vec2d&v) {
	double n = scr.norm();
	return scr * vec2d::inner_product(scr, v) / n / n;
}
vec2d vec2d::reflection(const vec2d&v, const line2d&l) {
	return (projection(vec2d(l.v1 - l.v0), vec2d(v - l.v0)) + l.v0) * 2 - v;
}
line2d::line2d(const vec2d&v0, const vec2d&v1) :v0(v0), v1(v1) {}
double line2d::length()const {
	return vec2d::distance(v0, v1);
}
vec2d line2d::cross_point(const line2d&l0, const line2d&l1) {
	return vec2d(((l0.v1.x * l0.v0.y - l0.v0.x * l0.v1.y) * (l1.v1.x - l1.v0.x) - (l1.v1.x * l1.v0.y - l1.v0.x * l1.v1.y) * (l0.v1.x - l0.v0.x)) / ((l0.v1.x - l0.v0.x) * (l1.v1.y - l1.v0.y) - (l1.v1.x - l1.v0.x) * (l0.v1.y - l0.v0.y)), ((l0.v1.x * l0.v0.y - l0.v0.x * l0.v1.y) * (l1.v1.y - l1.v0.y) - (l1.v1.x * l1.v0.y - l1.v0.x * l1.v1.y) * (l0.v1.y - l0.v0.y)) / ((l0.v1.x - l0.v0.x) * (l1.v1.y - l1.v0.y) - (l1.v1.x - l1.v0.x) * (l0.v1.y - l0.v0.y)));
}
bool line2d::is_cross(const line2d&l0, const line2d&l1) {
	vec2d cross = cross_point(l0, l1);
	if (std::isnan(cross.x) && std::isnan(cross.y)) {
		double dis_sum = vec2d::distance(l0.v0, l0.v1) + vec2d::distance(l1.v0, l1.v1);
		double dis_max = max({ vec2d::distance(l0.v0, l0.v1),vec2d::distance(l0.v0, l1.v0),vec2d::distance(l0.v0, l1.v1),vec2d::distance(l0.v1, l1.v0),vec2d::distance(l0.v1, l1.v1),vec2d::distance(l1.v0, l1.v1) });
		return dis_max < dis_sum || abs(dis_max - dis_sum) < EPS;
	} else if (std::isinf(cross.x) || std::isinf(cross.y)) {
		return false;
	} else {
		vec2d v_l0 = l0.v1 - l0.v0;
		vec2d cross_l0 = cross - l0.v0;
		double ks0 = v_l0.x != 0 ? cross_l0.x / v_l0.x : cross_l0.y / v_l0.y;
		vec2d v_l1 = l1.v1 - l1.v0;
		vec2d cross_l1 = cross - l1.v0;
		double ks1 = v_l1.x != 0 ? cross_l1.x / v_l1.x : cross_l1.y / v_l1.y;
		return 0 <= ks0 && ks0 <= 1 && 0 <= ks1 && ks1 <= 1;
	}
}
double line2d::distance(const vec2d&v, const line2d&l) {
	line2d l_ref(v, vec2d::reflection(v, l));
	return is_cross(l, l_ref) ? l_ref.length() / 2 : min({ vec2d::distance(v,l.v0),vec2d::distance(v,l.v1) });
}
double line2d::distance(const line2d&l0, const line2d&l1) {
	return is_cross(l0, l1) ? 0 : min({ distance(l0.v0,l1),distance(l0.v1,l1),distance(l1.v0,l0),distance(l1.v1,l0) });
}

