		double d3 = DistPoint2Seg(P[2], P[0], P[1]);
		double d4 = DistPoint2Seg(P[3], P[0], P[1]);
		printf("%.10f\n", min(d1, min(d2, min(d3, d4))));
	}
	return 0;
}

