        cin >> x >> y; l.s = P(x, y);
        cin >> x >> y; l.t = P(x, y);
        cin >> x >> y; m.s = P(x, y);
        cin >> x >> y; m.t = P(x, y);
        cout << distSS(l, m) << endl;
    }
