struct Point {
    int x, y;

    Point() = default;
    Point(int x, int y) : x(x), y(y) {}

    Point operator+(const Point &rhs) const {
        return Point(x + rhs.x, y + rhs.y);
    }

    Point operator-(const Point &rhs) const {
        return Point(x - rhs.x, y - rhs.y);
    }

    long long operator*(const Point &rhs) const {
        return 1ll * x * rhs.x + 1ll * y * rhs.y;
    }

    long long operator%(const Point &rhs) const {
        return 1ll * x * rhs.y - 1ll * y * rhs.x;
    }
};

bool intersect(Point p1, Point p2, Point p3, Point p4) {
    if (max(p1.x, p2.x) < min(p3.x, p4.x) || max(p3.x, p4.x) < min(p1.x, p2.x)) return false;
    if (max(p1.y, p2.y) < min(p3.y, p4.y) || max(p3.y, p4.y) < min(p1.y, p2.y)) return false;
    return ((p3 - p1) % (p4 - p1)) * ((p3 - p2) % (p4 - p2)) <= 0 &&
           ((p1 - p3) % (p2 - p3)) * ((p1 - p4) % (p2 - p4)) <= 0;
}

double dist(Point p1, Point p2, Point p3, Point p4) {
    if (intersect(p1, p2, p3, p4)) return 0;
    double res = sqrt(min({(p1 - p3) * (p1 - p3), (p2 - p3) * (p2 - p3), (p1 - p4) * (p1 - p4), (p2 - p4) * (p2 - p4)}));
    if ((p1 - p3) * (p4 - p3) >= 0 && (p1 - p4) * (p3 - p4) >= 0) 
        res = min(res, abs((p1 - p3) % (p4 - p3)) / sqrt((p4 - p3) * (p4 - p3)));
    if ((p2 - p3) * (p4 - p3) >= 0 && (p2 - p4) * (p3 - p4) >= 0) 
        res = min(res, abs((p2 - p3) % (p4 - p3)) / sqrt((p4 - p3) * (p4 - p3)));
    if ((p3 - p1) * (p2 - p1) >= 0 && (p3 - p2) * (p1 - p2) >= 0) 
        res = min(res, abs((p3 - p1) % (p2 - p1)) / sqrt((p2 - p1) * (p2 - p1)));
    if ((p4 - p1) * (p2 - p1) >= 0 && (p4 - p2) * (p1 - p2) >= 0) 
        res = min(res, abs((p4 - p1) % (p2 - p1)) / sqrt((p2 - p1) * (p2 - p1)));
    return res;
}

