struct Point
{
    double x,y;
    Point(){}
    Point(double _x,double _y):x(_x),y(_y){}
    Point operator -(Point p){return Point(x-p.x,y-p.y);}

    double norm(){return x*x+y*y;}
    double abs(){return sqrt(norm());}


};
double dot(Point a,Point b)
{
    return a.x*b.x+a.y*b.y;
}
double cross(Point a,Point b)
{
    return a.x*b.y-a.y*b.x;
}
double dist(Point a,Point b)
{
    return sqrt((a.x-b.x)*(a.x-b.x)+(a.y-b.y)*(a.y-b.y));
}
int n;
Point p1,p2,p3,p4;
bool is_intersect(Point p1,Point p2,Point p3,Point p4)
{
    Point a=p2-p1;
    Point b=p4-p3;
    return cross(p1,b)*cross(p2,b)<0&&cross(p3,a)*cross(p4,a)<0;
}
double dist_pl(Point p,Point lp1,Point lp2)
{
    Point a=p-lp1;
    Point b=lp2-lp1;
    if(dot(p-lp1,lp2-lp1)<0) return dist(p,lp1);
    if(dot(p-lp2,lp1-lp2)<0) return dist(p,lp2);
    return fabs(cross(a,b)/b.abs());
}
double dist_ll(Point p1,Point p2,Point p3,Point p4)
{
    if(is_intersect(p1,p2,p3,p4))
    {
        return 0;

    }
    return min(min(dist_pl(p1,p3,p4),dist_pl(p2,p3,p4)),min(dist_pl(p3,p1,p2),dist_pl(p4,p1,p2)));

}
