int ccw(Point &p0, Point &p1, Point &p2)
{
    auto a = p1 - p0;
    auto b = p2 - p0;

    auto cross_z = a.cross(b);

    if (cross_z > 0.0)
        return 1;
    else if (cross_z < 0.0)
        return -1;
    else
    {
        auto dot_prod = a.dot(b);
        if (dot_prod < 0.0)
            return 100;
        else if (b.abs() > a.abs())
            return -100;
        else
        {
            return 0;
        }
    }
}

bool intersection(Segment &s1, Segment &s2)
{
    return ccw(s1.p1, s1.p2, s2.p1) * ccw(s1.p1, s1.p2, s2.p2) <= 0.0 &&
           ccw(s2.p1, s2.p2, s1.p1) * ccw(s2.p1, s2.p2, s1.p2) <= 0.0;
}

double distance(Point &a, Point &b)
{
    return (a - b).abs();
}

double distance(Point &p, Line &l)
{
    auto q = l.p1 - l.p2;
    auto r = p - l.p2;
    return fabs(r.cross(q)) / q.abs();
}

double distance(Point &p, Segment &s)
{
    auto q1 = s.p2 - s.p1;
    auto q2 = p - s.p1;
    auto r1 = s.p1 - s.p2;
    auto r2 = p - s.p2;
    if (q1.dot(q2) >= 0.0 && r1.dot(r2) >= 0.0)
    {
        Line l(s);
        return distance(p, l);
    }
    else
    {
        return std::min(distance(p, s.p1), distance(p, s.p2));
    }
}

double distance(Segment &s1, Segment &s2)
{
    if (intersection(s1, s2))
        return 0.0;
    return std::min(std::min(distance(s1.p1, s2), distance(s1.p2, s2)),
                    std::min(distance(s2.p1, s1), distance(s2.p2, s1)));
}

