    public static class MathUtil
    {
        public static double Dot(Vector v1, Vector v2)
        {
            return v1.X * v2.X + v1.Y * v2.Y;
        }

        public static double Cross(Vector v1, Vector v2)
        {
            return v1.X * v2.Y - v1.Y * v2.X;
        }

        public static bool IsOrthogonal(Vector v1, Vector v2)
        {
            return Dot(v1, v2) == 0.0;
        }

        public static bool IsOrthogonal(Point a1, Point a2, Point b1, Point b2)
        {
            return IsOrthogonal(a1 - a2, b1 - b2);
        }

        public static bool IsOrthogonal(Segment s1, Segment s2)
        {
            return IsOrthogonal((Vector)(s1.P1 - s1.P2), (Vector)(s2.P1 - s2.P2));
        }

        public static bool IsParallel(Vector v1, Vector v2)
        {
            return Cross(v1, v2) == 0.0;
        }

        public static bool IsParallel(Point a1, Point a2, Point b1, Point b2)
        {
            return IsParallel(a1 - a2, b1 - b2);
        }
        public static bool IsParallel(Segment s1, Segment s2)
        {
            return IsParallel((Vector)(s1.P1 - s1.P2), (Vector)(s2.P1 - s2.P2));
        }

        public static Vector Project(Vector v, Vector onNormal)
        {
            // mag = |b|cos?? / |a|
            //     = |a||b|cos?? / |a|^2
            double mag = Dot(v, onNormal) / onNormal.Norm();
            return new Vector(onNormal.X * mag, onNormal.Y * mag);
        }

        public static Point Project(Point p, Segment s)
        {
            Vector v = p - s.P1;
            Vector baseV = s.P2 - s.P1;
            Vector project = Project(v, baseV);
            return s.P1 + project;
        }

        public static Point Reflect(Point p, Segment s)
        {
            Point project = Project(p, s);
            return p + (project - p) * 2.0;
        }

        // ??????????????????????¨??????? ~ ????¨????????????¢???????????°???
        public const int OnLineBack = 2;
        public const int CounterClockwise = 1;
        public const int OnSegment = 0;
        public const int Clockwise = -1;
        public const int OnLineFront = -2;

        /// <summary>
        /// ??????p0p1??????????????????p0p2??????????¨??????? ~ ????¨?????????????????????????????????????int??§?????????
        /// </summary>
        /// <param name="p0"></param>
        /// <param name="p1"></param>
        /// <param name="p2"></param>
        /// <returns></returns>
        public static int Ccw(Point p0, Point p1, Point p2)
        {
            var baseV = p1 - p0;
            var v = p2 - p0;
            if (Cross(baseV, v) > 0)
                return CounterClockwise;
            else if (Cross(baseV, v) < 0)
                return Clockwise;
            else if (Dot(baseV, v) < 0)
                return OnLineBack;
            else if (baseV.Norm() < v.Norm())
                return OnLineFront;
            return OnSegment;
        }

        /// <summary>
        /// ????????????????????´??????????????????????¢?????????????
        /// http://www5d.biglobe.ne.jp/~tomoya03/shtml/algorithm/Intersection.htm
        /// </summary>
        /// <param name="p1"></param>
        /// <param name="p2"></param>
        /// <param name="p3"></param>
        /// <param name="p4"></param>
        /// <returns></returns>
        public static bool Intersect(Segment s1, Segment s2)
        {
            return Intersect(s1.P1, s1.P2, s2.P1, s2.P2);
        }

        public static bool Intersect(Point p1, Point p2, Point p3, Point p4)
        {
            return (
                Ccw(p1, p2, p3) * Ccw(p1, p2, p4) <= 0 &&
                Ccw(p3, p4, p1) * Ccw(p3, p4, p2) <= 0
            );
        }

        public static double GetDistancePP(Point p1, Point p2)
        {
            return (p1 - p2).Abs();
        }

        public static double GetDistancePL(Point p, Line l)
        {
            var v1 = p - l.P1;
            var v2 = l.P2 - l.P1;

            var area = Math.Abs(Cross(v1, v2));
            var bottom = v2.Abs();
            return area / bottom;
        }

        public static double GetDistancePS(Point p, Segment s)
        {
            // p??¨???????????????????????????????????????????§????90???????????§????????´??????????????¨???????????¢???
            if (Dot(p - s.P1, s.P2 - s.P1) < 0.0)
                return GetDistancePP(p, s.P1);
            if (Dot(p - s.P2, s.P1 - s.P2) < 0.0)
                return GetDistancePP(p, s.P2);

            // ????????\??????????????¨??´???????????¢???
            return GetDistancePL(p, s);
        }
        public static double GetDistanceSS(Segment s1, Segment s2)
        {
            // ?????????????????????????????¢???0???
            if (Intersect(s1, s2))
                return 0.0;

            // ????????\????????´??????????????????????????¨????????????????????¢?????????????????????????°????????????????
            return Math.Min(
                Math.Min(GetDistancePS(s1.P1, s2), GetDistancePS(s1.P2, s2)),
                Math.Min(GetDistancePS(s2.P1, s1), GetDistancePS(s2.P2, s1))
            );
        }
        public static double GetDistanceLL(Line l1, Line l2)
        {
            // ?????????????????????????????¢???0???
            if (! IsParallel(l1, l2))
                return 0.0;
            return GetDistancePL(l1.P1, l2);
        }
    }
}

