public class Program
{
    public void Solve()
    {
        int NQ = Reader.Int();

        for (int q = 0; q < NQ; q++)
        {
            var a = new Point(Reader.Int(), Reader.Int());
            var b = new Point(Reader.Int(), Reader.Int());
            var c = new Point(Reader.Int(), Reader.Int());
            var d = new Point(Reader.Int(), Reader.Int());
            var seg1 = new Segment(a, b);
            var seg2 = new Segment(c, d);
            Console.WriteLine(Distance(seg1, seg2));
        }
        Console.ReadLine();
    }

    public Point Projection(Point p, Segment seg)
    {
        Point v = seg.B - seg.A;
        return seg.A + v * (v.Dot(p - seg.A) / v.Dot(v));
    }

    public bool OnSegment(Point p, Segment s)
    {
        return (s.A - p).Det(s.B - p) == 0 && (s.A - p).Dot(s.B - p) <= 0;
    }
    public bool OnSegment(Point p, Point seg1, Point seg2)
    {
        return (seg1 - p).Det(seg2 - p) == 0 && (seg1 - p).Dot(seg2 - p) <= 0;
    }

    public double Distance(Point p, Segment seg)
    {
        var q = Projection(p, seg);
        if (OnSegment(q, seg)) return p.Distance(q);
        return Math.Min(p.Distance(seg.A), p.Distance(seg.B));
    }

    public double Distance(Segment seg1, Segment seg2)
    {
        if (SegmentIntersect(seg1, seg2)) return 0;
        return Math.Min(Math.Min(Distance(seg1.A, seg2), Distance(seg1.B, seg2)),
                        Math.Min(Distance(seg2.A, seg1), Distance(seg2.B, seg1)));
    }

    bool SegmentIntersect(Segment seg1, Segment seg2)
    {
        var c1 = (seg1.B - seg1.A).Det(seg2.A - seg1.A);
        if (c1 == 0 && (seg1.A - seg2.A).Dot(seg1.B - seg2.A) <= 0) return true;
        var c2 = (seg1.B - seg1.A).Det(seg2.B - seg1.A);
        if (c2 == 0 && (seg1.A - seg2.B).Dot(seg1.B - seg2.B) <= 0) return true;
        var c3 = (seg2.B - seg2.A).Det(seg1.A - seg2.A);
        if (c3 == 0 && (seg2.A - seg1.A).Dot(seg2.B - seg1.A) <= 0) return true;
        var c4 = (seg2.B - seg2.A).Det(seg1.B - seg2.A);
        if (c4 == 0 && (seg2.A - seg1.B).Dot(seg2.B - seg1.B) <= 0) return true;
        return c1 * c2 < 0 && c3 * c4 < 0;
    }


    public class Segment
    {
        public Point A, B;
        public Segment(Point a, Point b) { A = a; B = b; }
    }

    public struct Point : IEquatable<Point>, IComparable<Point>
    {
        public static readonly double Eps = 1e-10;
        public double X, Y;

