double distance(Vec2 a, Vec2 b, Vec2 c, Vec2 d) {

    double dist(Vec2 p, Vec2 a, Vec2 b) {
        auto ab = b - a;
        auto ap = p - a;
        if (ab.dot(ap) < 0)
            return ap.mag();

        auto ba = a - b;
        auto bp = p - b;
        if (ba.dot(bp) < 0)
            return bp.mag();

        return abs(ab.cross(ap)) / ab.mag();
    }

    if (isIntersectLineSegments(a, b, c, d))
        return 0;

    return min(dist(c, a, b), dist(d, a, b), dist(a, c, d), dist(b, c, d));
}

