        self.p1 = p1
        self.p2 = p2

def ccw(p0, p1, p2):
    a = p1 - p0
    b = p2 - p0
    if a.cross(b) > EPS:
        return 1
    elif a.cross(b) < -EPS:
        return -1
    elif a.dot(b) < -EPS:
        return 2
    elif a.norm() < b.norm():
        return -2
    else:
        return 0

def intersect(*args, **kwargs):
    # input two segments
    if len(args) == 2:
        s1, s2 = args
        return (ccw(s1.p1, s1.p2, s2.p1) * ccw(s1.p1, s1.p2, s2.p2) <= 0 and
                    ccw(s2.p1, s2.p2, s1.p1) * ccw(s2.p1, s2.p2, s1.p2) <= 0)
    # input four points
    elif len(args) == 4:
        p0, p1, p2, p3 = args
        return (ccw(p0, p1, p2) * ccw(p0, p1, p3) <= 0 and
                    ccw(p2, p3, p0) * ccw(p2, p3, p1) <= 0)
    else:
        raise ArgsError

def get_distance_lp(l, p):
    v1 = l.p1 - l.p2
    v2 = p - l.p1
    return abs(v1.cross(v2))/v1.abs()

def get_distance_sp(s, p):
    v1 = s.p2 - s.p1
    v2 = p - s.p1
    v3 = s.p1 - s.p2
    v4 = p - s.p2
    if v1.dot(v2) < 0:
        return v2.abs()
    elif v3.dot(v4):
        return v4.abs()
    else:
        return get_distance_lp(s, p)

