    # input two points with the startpoint (0, 0)
    if len(args) == 2:
        p1, p2 = args
        v = p1 - p2
        return v.abs()
    # input two segments
    elif len(kwargs) == 2:
        s1 = kwargs['s1']
        s2 = kwargs['s2']
        if intersect(s1, s2):
            return 0.0
        else:
            return min(min(get_distance_sp(s1, s2.p1), get_distance_sp(s1, s2.p2)),
                        min(get_distance_sp(s2, s1.p1), get_distance_sp(s2, s1.p2)))
    else:
        raise ArgsError

