def pairwise_min2(a, b, c):
    return min(b.abs2(), c.abs2(),
               (b - a).abs2(), (c - a).abs2())


def calc3(a, b, c):
    c_ab = a.cross(b)
    c_ac = a.cross(c)
    if c_ab == 0 and c_ac == 0:  # on line
        s = Fraction(a.dot(b), a.abs2())
        t = Fraction(a.dot(c), a.abs2())
        if s < 0 and t < 0:
            return (max(s, t) * a).abs2()
        if s > 1 and t > 1:
            return ((min(s, t) - 1) * a).abs2()
        return 0

    cb = c - b
    c_acb = a.cross(cb)
    if c_acb != 0:  # not parallel
        u = Fraction(b.cross(cb), c_acb)
        v = Fraction(b.cross(a), c_acb)
        if 0 <= u <= 1 and 0 <= v <= 1:
            return 0  # crossed

    d_cba = cb.dot(a)
    if d_cba == 0:  # orthogonal
        s = Fraction(b.dot(a), a.abs2())
        if 0 <= s <= 1:
            sa = s * a
            return min((b - sa).abs2(), (c - sa).abs2())
        return pairwise_min2(a, b, c)

    d_ba = b.dot(a)
    d_ca = c.dot(a)
    a2 = a.abs2()
    s = Fraction(d_ba, a2)
    t = Fraction(d_ca, a2)
    p = Fraction(-d_ba, d_cba)
    q = Fraction(a2 - d_ba, d_cba)

    dists = [pairwise_min2(a, b, c)]
    if 0 <= s <= 1:
        dists.append((b - s * a).abs2())
    if 0 <= t <= 1:
        dists.append((c - t * a).abs2())
    if 0 <= p <= 1:
        dists.append((b + p * cb).abs2())
    if 0 <= q <= 1:
        dists.append((b + q * cb - a).abs2())

    return min(dists)


def solve(u, v, w, x):
    return math.sqrt(min(calc3(v - u, w - u, x - u),
                         calc3(x - w, u - w, v - w)))


