from itertools import starmap


def cross(a: complex, b: complex) -> float:
    return float(a.real * b.imag - a.imag * b.real)


def is_intersected(p0: complex, p1: complex, p2: complex, p3: complex) -> bool:
    max_x1, min_x1 = (p0.real, p1.real) if p0.real > p1.real else (p1.real, p0.real)
    max_x2, min_x2 = (p2.real, p3.real) if p2.real > p3.real else (p3.real, p2.real)
    if max_x1 < min_x2 or max_x2 < min_x1:
        return False
    max_y1, min_y1 = (p0.imag, p1.imag) if p0.imag > p1.imag else (p1.imag, p0.imag)
    max_y2, min_y2 = (p2.imag, p3.imag) if p2.imag > p3.imag else (p3.imag, p2.imag)
    if max_y1 < min_y2 or max_y2 < min_y1:
        return False

    return (cross(p1 - p0, p2 - p0) * cross(p1 - p0, p3 - p0) < 1e-6
            and cross(p3 - p2, p0 - p2) * cross(p3 - p2, p1 - p2) < 1e-6)


def point_to_segment(p0: complex, p1: complex, p2: complex) -> float:
    prod = (p1 - p0).conjugate() * (p2 - p0)
    if prod.real <= 1e-6:
        return abs(p2 - p0)
    l = abs(p1 - p0)  # noqa: E741
    d = prod.real / l
    if d >= l:
        return abs(p2 - p1)
    return abs(prod.imag) / l


