pub type Point = Complex<f64>;
#[derive(Clone, Debug, PartialEq)]
pub struct LineSegment {
    p1: Point,
    p2: Point,
}
impl LineSegment {
    pub fn new(p1: Point, p2: Point) -> Self {
        LineSegment { p1: p1, p2: p2 }
    }
    pub fn dir(&self) -> Point {
        self.p2 - self.p1
    }
    pub fn ccw(&self, p: Point) -> CCW {
        ccw(self.p1, self.p2, p)
    }
    pub fn projection(&self, p: Point) -> Point {
        let e = self.dir().unit();
        self.p1 + e * (p - self.p1).dot(e)
    }
    pub fn reflection(&self, p: Point) -> Point {
        p + (self.projection(p) - p) * 2.0
    }
    pub fn is_parallel(&self, other: &Self) -> bool {
        Real(self.dir().cross(other.dir())) == Real(0.)
    }
    pub fn is_orthogonal(&self, other: &Self) -> bool {
        Real(self.dir().dot(other.dir())) == Real(0.)
    }
    pub fn intersect(&self, other: &Self) -> bool {
        self.ccw(other.p1) as i8 * self.ccw(other.p2) as i8 <= 0
            && other.ccw(self.p1) as i8 * other.ccw(self.p2) as i8 <= 0
    }
    pub fn intersect_point(&self, p: Point) -> bool {
        self.ccw(p) == CCW::OnSegment
    }
    pub fn cross_point(&self, other: &Self) -> Option<Point> {
        if self.intersect(other) {
            let a = self.dir().cross(other.dir());
            let b = self.dir().cross(self.p2 - other.p1);
            if Real(a.abs()) == Real(0.) && Real(b.abs()) == Real(0.) {
                Some(other.p1)
            } else {
                Some(other.p1 + (other.dir() * b / a))
            }
        } else {
            None
        }
    }
    pub fn distance_point(&self, p: Point) -> f64 {
        let r = self.projection(p);
        if self.intersect_point(r) {
            (r - p).abs()
        } else {
            std::cmp::min(TotalOrd((self.p1 - p).abs()), TotalOrd((self.p2 - p).abs())).0
        }
    }
    pub fn distance(&self, other: &Self) -> f64 {
        use std::cmp::min;
        if self.intersect(other) {
            0.
        } else {
            min(
                min(
                    TotalOrd(self.distance_point(other.p1)),
                    TotalOrd(self.distance_point(other.p2)),
                ),
                min(
                    TotalOrd(other.distance_point(self.p1)),
                    TotalOrd(other.distance_point(self.p2)),
                ),
            )
            .0
        }
    }
}

pub const EPS: f64 = 1e-8;
#[derive(Clone, Debug)]
pub struct Real(pub f64);
impl PartialEq for Real {
    fn eq(&self, other: &Real) -> bool {
        (self.0 - other.0).abs() < EPS
    }
}
impl PartialOrd for Real {
    fn partial_cmp(&self, other: &Real) -> Option<std::cmp::Ordering> {
        if self == other {
            Some(std::cmp::Ordering::Equal)
        } else {
            self.0.partial_cmp(&other.0)
        }
    }
}

#[derive(Clone, Copy, Debug, PartialEq, Eq, PartialOrd, Ord)]
pub enum CCW {
    #[doc = " a--b--c"]
    OnlineFront = -2,
    #[doc = " a--b-vc"]
    Clockwise = -1,
    #[doc = " a--c--b"]
    OnSegment = 0,
    #[doc = " a--b-^c"]
    CounterClockwise = 1,
    #[doc = " c--a--b"]
    OnlineBack = 2,
}
pub fn ccw(a: Point, b: Point, c: Point) -> CCW {
    let x = b - a;
    let y = c - a;
    if Real(x.cross(y)) > Real(0.) {
        CCW::CounterClockwise
    } else if Real(x.cross(y)) < Real(0.) {
        CCW::Clockwise
    } else if Real(x.dot(y)) < Real(0.) {
        CCW::OnlineBack
    } else if Real(x.abs()) < Real(y.abs()) {
        CCW::OnlineFront
    } else {
        CCW::OnSegment
    }
}
#[derive(PartialEq, PartialOrd)]
pub struct TotalOrd<T>(pub T);
impl<T: PartialEq> Eq for TotalOrd<T> {}
impl<T: PartialOrd> Ord for TotalOrd<T> {
    fn cmp(&self, other: &TotalOrd<T>) -> std::cmp::Ordering {
        self.0.partial_cmp(&other.0).unwrap()
    }
}
