    {
    }
};

using Polygon = std::vector<Point>;

auto swap = [](auto &a, auto &b) {
    auto tmp = a;
    a = b;
    b = tmp;
};

int cross_number(Segment &l, Point &p)
{
    Vector a = l.p1 - p;
    Vector b = l.p2 - p;

    if (a.y > b.y)
        swap(a, b);

    // If p is on the segment
    if (a.cross(b) == 0 && a.dot(b) <= 0)
        return -1;

    if (a.y <= 0 && b.y > 0 && a.cross(b) > 0)
        return 1;

    return 0;
}

int contain(Polygon &pol, Point &p)
{
    auto n = pol.size();
    Segment l;

    unsigned total_cross_number = 0;

    for (size_t i = 0; i <= n - 1; ++i)
    {
        l.p1 = pol.at(i);
        l.p2 = pol.at((i + 1) % n);

        auto ncross = cross_number(l, p);

        // If p is on the segment
        if (ncross == -1)
            return 1;

        total_cross_number += ncross;
    }

    return (total_cross_number % 2) * 2;
}

int main()
{
    int n;
    std::cin >> n;

    Polygon pol;
    Point p;
    for (size_t i = 0; i < n; ++i)
    {
        std::cin >> p.x >> p.y;
        pol.push_back(p);
    }

    int q;
    std::cin >> q;
    for (size_t i = 0; i < q; ++i)
    {
        std::cin >> p.x >> p.y;
        std::cout << contain(pol, p) << std::endl;
    }
}

