#define EQ(a, b) (abs((a) - (b)) < eps)
using P = complex<double>;      // position
struct L : vector<P> {
    L(const P &a = P(), const P &b = P()) {
        emplace_back(a); emplace_back(b);
    }
};
using Polygon = vector<P>;
ostream &operator<<(ostream &os, P p) { return os << real(p) << " " << imag(p); }
double dot(const P &a, const P &b) { return real(conj(a) * b); }
double cross(const P &a, const P &b) { return imag(conj(a) * b); }

enum { OUT, ON, IN };
int contains(const Polygon &ps, const P &p) {
    bool in = false;
    for (int i = 0; i < ps.size(); i++) {
        P a = ps[i] - p, b = ps[(i + 1) % ps.size()] - p;
        if (cross(a, b) == 0 && dot(a, b) <= 0) return ON;
        if (imag(a) > imag(b)) swap(a, b);
        if (imag(a) <= 0 && 0 < imag(b) && cross(a, b) < 0) in = !in;
    }
    return in ? IN : OUT;
}

