// 多角形
//   辺をメンバ関数に持たないためedge()はO(n)で処理している
#include<vector>
#include<algorithm>
#include<utility>
class Polygon {
public:
  void add(const Point& p) {vertex_.push_back(p);}
  int size() const {return vertex_.size();}
  Point vertex(const int n) const {return vertex_.at(n);}
  Segment edge(const int n) const {return Segment(vertex(n), vertex((n + 1) % size()));}
  const std::vector<Point>& vertex() const {return vertex_;}
  std::vector<Segment> edge() const {
    std::vector<Segment> result;
    for(int i = 0; i < size(); ++i) result.push_back(edge(i));
    return result;
  }
  bool touch(const Point& p) const {
    auto e = edge();
    return std::any_of(e.begin(), e.end(), [=](const Segment& s) {return ccw(s.initial, s.terminal, p) == ON_SEGMENT;});
  }
  bool include(const Point& p) const {  // intersect()という名前にしたいけどグローバルスコープのintersect()たちが隠蔽されて困った
    bool in = false;                    // p=(x,y)から(INF,y)への半直線と多角形とが交差する回数 mod 2
    for(const auto& e: edge()) {
      if(intersect(p, e)) return true;
      Point a = e.initial  - p;
      Point b = e.terminal - p;
      if(a.y > b.y) std::swap(a, b);
      if(a.y <= EPS && 0.0 < b.y && cross(a, b) < EPS) in = !in;
    }
    return in;
  }
private:
  std::vector<Point> vertex_;
};
