enum class Containment : int32_t { kIsNotContained = 0, kIsOnEdge, kIsContained };

class Polygon {
 public:
  Polygon() noexcept;
  ~Polygon() noexcept;

  void AddVertex(const Point2D &vertex) noexcept;
  Containment AskContainment(const Point2D &point) const noexcept;

 private:
  Polygon(const Polygon &obj) = delete;
  Polygon &operator=(const Polygon &obj) = delete;
  Polygon(Polygon &&obj) = delete;
  Polygon &operator=(Polygon &&obj) = delete;

 private:
  std::vector<Point2D> vertices_;
};

void CallPolygonPointContainment(std::istream &input_stream) noexcept {
  input_stream.tie(0);
  std::ios::sync_with_stdio(false);
  Polygon *polygon = new Polygon();
  try {
    int32_t number_of_vertices;
    input_stream >> number_of_vertices;
    for (int32_t i = 0; i < number_of_vertices; ++i) {
      int32_t x, y;
      input_stream >> x >> y;
      polygon->AddVertex(Point2D(x, y));
    }
    int32_t number_of_queries;
    input_stream >> number_of_queries;
    for (int32_t i = 0; i < number_of_queries; ++i) {
      int32_t x, y;
      input_stream >> x >> y;
      const Containment containment = polygon->AskContainment(Point2D(x, y));
      std::cout << static_cast<int32_t>(containment) << std::endl;
    }
  } catch (...) {
    std::cerr << "CallPolygonPointContainment()" << std::endl;
    // DO NOT THROW ERROR
  }
  delete polygon;
}

