        bool onseg = false;
        for (int i = 0; i < n; i++) {
            Vector u = g[i] - p;
            Vector v = g[(i + 1) % n] - p;
            //on segment
            if (dcmp(cross(u, v)) == 0 && dcmp(dot(u, v)) <= 0) {
                onseg = true;
                break;
            }
            if (u.y > v.y) {
                swap(u, v);
            }
            if (dcmp(v.y) >= 0 && dcmp(u.y) < 0 && dcmp(cross(u, v)) > 0) {
                count++;
            }
        }
        if (onseg) {
            cout << 1 << endl;
        }
        else if (count % 2 == 0) {
            cout << 0 << endl;
        }
        else {
            cout << 2 << endl;
        }
    }

