constexpr auto INF = 2147483647;
static const double EPS = pow(10,-10);
typedef long long ll;
static const int IN = 2;
static const int ON = 1;
static const int OUT = 0;


class Vector2{
  public:
  double x, y;
  Vector2(){}
  Vector2(double x, double y): x(x), y(y) {}

  Vector2 operator + (Vector2 v) {
    return Vector2(x + v.x , y + v.y);
  }
  Vector2 operator - (Vector2 v) {
    return Vector2(x - v.x , y - v.y);
  }

  Vector2 operator * (double d){
    return Vector2(d*x, d*y);
  }

  double norm(){
    return x*x + y*y;
  }

  double abs(){
    return sqrt(norm());
  }

};

double dot(Vector2 a, Vector2 b){
  return a.x*b.x + a.y*b.y;
}

double cross(Vector2 a, Vector2 b){
  return a.x*b.y - a.y*b.x;
}

int contain(Vector2 poly[], int n, Vector2 p){
  bool isContain = false;
  Vector2 a,b ;
  rep(i,n){
    a = poly[i] - p;
    b = poly[(i+1) % n] - p;
    if(abs(cross(a,b)) < EPS && dot(a,b) < EPS) return ON;
    if(a.y > b.y) swap(a,b);
    if(a.y < EPS && b.y > EPS && cross(a,b) > EPS) isContain = !isContain;
  }
  return (isContain ? IN: OUT);
}

