using namespace std;
static const int MAX_N = 100;
static const double EPS = 1e-10;

class Point
{
public:
	double x, y;

	Point(double x = 0, double y = 0): x(x), y(y) {}
	Point operator - (Point p) { return Point(x - p.x, y - p.y); }
};

typedef Point Vector;

Point g[MAX_N];
Point p;
int n;

double
norm(Vector a)
{
	return a.x * a.x + a.y * a.y;
}

double
abs(Vector a)
{
	return sqrt(norm(a));
}

double
dot(Vector a, Vector b)
{
	return a.x * b.x + a.y * b.y;
}


double
cross(Vector a, Vector b)
{
	return a.x * b.y - a.y * b.x;
}

int
solve()
{
	bool f = false;
	for (int i = 0; i < n; ++i)
	{
		Point a = g[i] - p;
		Point b = g[(i + 1) % n] - p;
		if (abs(cross(a, b)) < EPS && dot(a, b) < EPS)
			return 1;

		if (a.y > b.y)
			swap(a, b);

		if (a.y < EPS && EPS < b.y && cross(a, b) > EPS)
			f = !f;
	}

	return f ? 2 : 0;
}

int
main(int argc, char** argv)
{
	int x, y;
	int q;

	scanf("%d", &n);
	for (int i = 0; i < n; ++i)
	{
		scanf("%d %d", &x, &y);
		g[i].x = x, g[i].y = y;
	}

	scanf("%d", &q);
	while (q--)
	{
		scanf("%d %d", &x, &y);
		p.x = x, p.y = y;
		printf("%d\n", solve());
	}

	return 0;
}

