using namespace std;
typedef pair<double, double> pt;
const double e = 1e-8;
const double X = 1e4 + 1;

pt operator-(pt a, pt b) {
    return pt(a.x - b.x, a.y - b.y);
}

bool zero(double x) {
  return fabs(x) <= e;
}

bool le(double x, double y) {
  return x < y or fabs(x - y) <= e;
}

double cross(pt a, pt b) {
  return a.x*b.y - a.y*b.x;
}

bool ccw(pt a, pt b, pt c) {
  return cross(b - a, c - a) > 0;
}

double sq(double d) {
  return d * d;
}

double dist(pt A, pt B) {
  return sqrt(sq(A.x - B.x) + sq(A.y - B.y));
}

bool intersect(pt A, pt B, pt C, pt D) {
  return ccw(A, B, C) != ccw(A, B, D) and ccw(C, D, A) != ccw(C, D, B);
}

bool on_line(pt A, pt B, pt C) {
  return fabs(dist(A, C) + dist(B, C) - dist(A, B)) <= e;
}

int contains(vector<pt> shape, pt C) {

  int intersections = 0;
  bool on_segment = false;
  pt D = {C.x, X};

  for (int i = 0; i < shape.size(); i++) {

    pt A = shape[i];
    pt B = shape[(i + 1) % shape.size()];

    bool j = intersect(A, B, C, D);
    intersections += j;
    on_segment |= on_line(A, B, C);
    /*
    printf("(%.0f %.0f) - (%.0f %.0f): (%.1f %.1f) = %d\n",
        A.x, A.y, B.x, B.y, C.x, C.y, j
    );
    */
  }

  if (on_segment) return 1;
  else if (intersections % 2 == 0) return 0;
  else return 2;

}

