  vector<pt> shape;

  int g;
  cin >> g;
  for (int i = 0; i < g; ++i) {
    double x, y;
    cin >> x >> y;
    shape.emplace_back(x, y);
  }

  int q;
  cin >> q;
  for (int i = 0; i < q; ++i) {
    double x, y;
    cin >> x >> y;
    cout << contains(shape, {x, y}) << endl;
  }

  return 0;
}

