// taken from
// https://www.eecs.umich.edu/courses/eecs380/HANDOUTS/PROJ2/InsidePoly.html
double Angle2D(double x1, double y1, double x2, double y2) {
    double dtheta, theta1, theta2;
    theta1 = atan2(y1, x1);
    theta2 = atan2(y2, x2);
    dtheta = theta2 - theta1;
    while (dtheta > M_PI)
        dtheta -= 2 * M_PI;
    while (dtheta < -M_PI)
        dtheta += 2 * M_PI;
    return (dtheta);
}

int check(pt p) {
    double angle = 0;
    pt p1, p2;
    fo(i, 0, n) {
        p1 = poly[i] - p;
        p2 = poly[(i + 1) % n] - p;
        angle += Angle2D(p1.x, p1.y, p2.x, p2.y);
        if (dist(poly[i], p) + dist(poly[(i + 1) % n], p) ==
            dist(poly[i], poly[(i + 1) % n])) {
            return ON;
        }
    }
    if (fabs(angle) < M_PI) {
        return OUT;
    } else {
        return IN;
    }
}

