const let EPS = 1e-8;

int sign(let x) { return (x < -EPS) ? -1 : (x > EPS) ? 1 : 0; }

struct Point {
  let x, y;

  friend Point operator - (Point a, Point b) {
    return (Point) {a.x - b.x, a.y - b.y};
  }
  Point operator * (let k) { return (Point) {x * k, y * k}; }

  friend bool operator == (Point a, Point b) {
    return sign(a.x - b.x) == 0 && sign(a.y - b.y) == 0;
  }

  let dist() {
    return sqrt(x * x + y * y);
  }

  friend let Times(Point a, Point b) {
    return a.x * b.y - a.y * b.x;
  }

  Point Rotate(let sink, let cosk) {
    return (Point) {
      x * cosk - y * sink, x * sink + y * cosk
    };
  }

  Point Rotate(let k) {
    return Rotate(sin(k), cos(k));
  }
};

typedef Point Vec;

struct Segment {
  Point s, e;
  Vec v;
  Segment() {}
  Segment(Point a, Point b) {
    s = a, e = b, v = b - a;
  }
  Segment(Point a, Point b, Vec c) {
    s = a, e = b, v = c;
  }

  let CrossX() {
    let res = (e - v * (e.y / v.y)).x;
    return res;
  }

  friend bool InSegment(Segment s, Point p) {
    if (sign(Times(s.v, p - s.s)) == 0) {
      if (sign(p.x - min(s.s.x, s.e.x)) < 0) return false;
      if (sign(p.y - min(s.s.y, s.e.y)) < 0) return false;
      if (sign(p.x - max(s.s.x, s.e.x)) > 0) return false;
      if (sign(p.y - max(s.s.y, s.e.y)) > 0) return false;
      return true;
    }
    return false;
  }
};

struct Polygon {
  vector<Point> p;

  void Add(Point x) { p.push_back(x); }

  Point &operator [] (int k) { return p.at(k); }

  Polygon operator - (Vec k) {
    Polygon x;
    for (size_t i = 0; i < this->p.size(); ++i)
      x.Add(this->p.at(i) - k);
    return x;
  }

  Polygon Rotate(let sink, let cosk) {
    Polygon x;
    for (size_t i = 0; i < this->p.size(); ++i)
      x.Add(this->p.at(i).Rotate(sink, cosk));
    return x;
  }

  Polygon Rotate(let k) {
    return Rotate(sin(k), cos(k));
  }

  Polygon Rotate(Vec k) {
    let z = k.dist();
    return Rotate(k.y / z, k.x / z);
  }

  friend int CheckPIP(Polygon poly, Point p) {
    poly.Add(poly[0]);
    for (size_t i = 0; i < poly.p.size() - 1; ++i) {
      if (InSegment((Segment) {poly[i], poly[i + 1]}, p)) return 1;
    }

    Polygon x = poly - p;
    int have = 0;
    for (size_t i = 0; i < x.p.size() - 1; ++i) {
      if (sign(x[i].x) < 0 && sign(x[i + 1].x) < 0) continue;
      if (sign(x[i].y) > 0 && sign(x[i + 1].y) > 0) continue;
      if (sign(x[i].y) < 0 && sign(x[i + 1].y) < 0) continue;
      if (sign(((Segment) {x[i], x[i + 1]}).CrossX()) < 0) continue;
      have++;
    }
    if (have & 1) return 2;
    return 0;
  }

};

Point ReadPoint() {
  Point p;
  scanf("%Lf%LF", &p.x, &p.y);
  return p;
}

Polygon ReadPolygon() {
  Polygon poly;
  int g = read();
  for (int i = 1; i <= g; ++i)
    poly.Add(ReadPoint());
  return poly;
}

