	int n;cin>>n;
	vector<P> ps(n);
	rep(i,0,n) ps[i].read();
	int m;cin>>m;
	rep(i,0,m){
		P p;p.read();cout<<contain(ps,p)<<endl;
	}
	return 0;
}

#include <iostream>
