struct Point{
	double px;
	double py;
};

Point Minus(Point a, Point b){
	Point c;
	c.px=a.px-b.px;
	c.py=a.py-b.py;
	return c;
}

double dot(Point a, Point b){
	return a.px*b.px+a.py*b.py;
}

int crs(Point a, Point b){
	double t=a.px*b.py-a.py*b.px;
	if(abs(t)<(1e-08))return 0;
	if(t<0)return -1;
	return 1;
}

int con(vector<Point> V, Point p){
	bool in=false;
	for(int i=0;i<V.size();i++){
		Point a = Minus(V[i],p);
		Point b = Minus(V[(i+1)%(V.size())],p);
		if(a.py>b.py)swap(a,b);
		if(a.py<=0 && 0<b.py){
			if(crs(a,b)<0)in=!in;
		}
		if(crs(a,b)==0 && dot(a,b)<=0)return 1;
	}
	return in ? 2 : 0;

}

int main(){
	vector<Point>v;
	Point P;
	int n;
	cin>>n;
	for(int i=0;i<n;i++){
		cin>>P.px>>P.py;
		v.push_back(P);
	}
	cin>>n;
	for(int i=0;i<n;i++){
		cin>>P.px>>P.py;
		cout<<con(v,P)<<endl;
	}


	return 0;
}

#include <bits/stdc++.h>
using namespace std;

#define db long double
const db eps = 0;
int sgn(db a, db b = 0) {
	a -= b;
	return (a > eps) - (a < -eps);
}

struct poi {
	db x, y;
	void r() { cin >> x; cin >> y; }
	void w() { cout << x << ' ' << y << '\n'; }
	poi operator -(poi p) {
		return {x - p.x, y - p.y};
	}
	poi operator +(poi p) {
		return {x + p.x, y + p.y};
	}
	poi operator *(db d) {
		return {x * d, y * d};
	}
	poi operator /(db d) {
		return {x / d, y / d};
	}
	bool operator ==(poi p) {
		return !sgn(x, p.x) && !sgn(y, p.y);
	}
	db dot(poi p) {
		return x * p.x + y * p.y;
	}
	db cross(poi p) {
		return x * p.y - y * p.x;
	}
	db len() { return sqrt(x * x + y * y); }
	db len2() { return x * x + y * y; }
	poi proj(poi p, poi q) {
		db s = (*this - p).dot(q - p) / (q - p).len2();
		return p + (q - p) * s;
	}
	db dis(poi p, poi q) {
		if((*this - p).dot(q - p) < 0) return (*this - p).len();
		if((*this - q).dot(p - q) < 0) return (*this - q).len();
		return abs((*this - p).cross(q - p) / (q - p).len());
	}
};
db xmul(poi a, poi b, poi c) {
	return (b - a).cross(c - a);
}

int ccw(poi a, poi b, poi c) {
	poi u = b - a, v = c - a;
	if(u.cross(v) > 0) return 1;
	if(u.cross(v) < 0) return -1;
	if(u.dot(v) < 0) return -2;
	if(u.len2() < v.len2()) return 2;
	return 0;
}
bool si(poi a, poi b, poi c, poi d) {
	return ccw(a, b, c) * ccw(a, b, d) <= 0 &&
		ccw(c, d, a) * ccw(c, d, b) <= 0;
}
poi li(poi a, poi b, poi c, poi d) {
	db u = xmul(c, d, a), v = xmul(c, d, b);
	return (a * v - b * u) / (v - u);
}

